import React, { useState } from "react";
import { AuthProvider, useAuth } from "./context/AuthContext";
import Login from "./components/Auth/Login";
import Register from "./components/Auth/Register";
import OTP from "./components/Auth/OTP";
import PaymentPanel from "./components/Payment/PaymentPanel";

const AppContent = () => {
  const { token, setToken } = useAuth();
  const [step, setStep] = useState<"login" | "register" | "otp" | "payment">("login");

  // Oturum kapatıldığında login ekranına dön
  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem("token");
    setStep("login");
  };

  if (!token) {
    if (step === "login") return <Login onRegister={() => setStep("register")} onOTP={() => setStep("otp")} />;
    if (step === "register") return <Register onLogin={() => setStep("login")} />;
    if (step === "otp") return <OTP onSuccess={() => setStep("payment")} />;
  }
  return <PaymentPanel onLogout={handleLogout} />;
};

const App = () => (
  <AuthProvider>
    <AppContent />
  </AuthProvider>
);

export default App;
